mapper.sql:
select

from t_region_set where 1



	and ${key} is null


	and ${key} = #{value}




	order by #{_orderBy}

mapper.select:
select 
	rs.id 
	, rs.nearby_range 
	, rs.delete_status 
	, rs.tx_code 
	, rs.create_user_id 
	, rs.create_by 
	, rs.create_date 
	, rs.update_user_id 
	, rs.update_by 
	, rs.update_date 
from t_region_set rs 
where rs.delete_status = 1

	and rs.id = #{id}


	and rs.nearby_range = #{nearby_range}


	and rs.tx_code like concat('%', #{tx_code}, '%')


	and rs.create_user_id = #{create_user_id}


	and rs.create_by like concat('%', #{create_by}, '%')


	and date_format(rs.create_date, '%Y-%m-%d') >= #{create_date_from}


	and date_format(rs.create_date, '%Y-%m-%d') <= #{create_date_to}


	and date_format(rs.create_date, '%Y-%m-%d') = #{create_date}


	and rs.update_user_id = #{update_user_id}


	and rs.update_by like concat('%', #{update_by}, '%')


	and date_format(rs.update_date, '%Y-%m-%d') >= #{update_date_from}


	and date_format(rs.update_date, '%Y-%m-%d') <= #{update_date_to}


	and date_format(rs.update_date, '%Y-%m-%d') = #{update_date}

order by rs.id desc

mapper.insert:
insert into t_region_set 


	${key}


values


	if(length(#{value}) = 0, null, #{value})

mapper.delete:
delete from t_region_set where 1



	and ${key} is null


	and ${key} = #{value}
